		$args=array();
		if(!session_questions_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `session_questions` set       `session_id`=' . (($data['session_id'] !== '' && $data['session_id'] !== NULL) ? "'{$data['session_id']}'" : 'NULL') . ', `question_id`=' . (($data['question_id'] !== '' && $data['question_id'] !== NULL) ? "'{$data['question_id']}'" : 'NULL') . ', `question_type`=' . (($data['question_type'] !== '' && $data['question_type'] !== NULL) ? "'{$data['question_type']}'" : 'NULL') . ', `order`=' . (($data['order'] !== '' && $data['order'] !== NULL) ? "'{$data['order']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"session_questions_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: session_questions_after_insert
	if(function_exists('session_questions_after_insert')){
		$res = sql("select * from `session_questions` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
