function speakers_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('speakers');
	if(!$arrPerm[1]){
		return false;
	}

	$data['full_name'] = makeSafe($_REQUEST['full_name']);
		if($data['full_name'] == empty_lookup_value){ $data['full_name'] = ''; }
	$data['session_id'] = makeSafe($_REQUEST['session_id']);
		if($data['session_id'] == empty_lookup_value){ $data['session_id'] = ''; }

	// hook: speakers_before_insert
	if(function_exists('speakers_before_insert')){
		$args=array();
		if(!speakers_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `speakers` set       `full_name`=' . (($data['full_name'] !== '' && $data['full_name'] !== NULL) ? "'{$data['full_name']}'" : 'NULL') . ', `session_id`=' . (($data['session_id'] !== '' && $data['session_id'] !== NULL) ? "'{$data['session_id']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"speakers_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: speakers_after_insert
	if(function_exists('speakers_after_insert')){
		$res = sql("select * from `speakers` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!speakers_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('speakers', $recID, getLoggedMemberID());

	return $recID;
}

function speakers_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('speakers');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='speakers' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='speakers' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: speakers_before_delete
	if(function_exists('speakers_before_delete')){
		$args=array();
		if(!speakers_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `speakers` where `id`='$selected_id'", $eo);

	// hook: speakers_after_delete
	if(function_exists('speakers_after_delete')){
		$args=array();
		speakers_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='speakers' and pkValue='$selected_id'", $eo);
}

function speakers_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('speakers');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='speakers' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='speakers' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['full_name'] = makeSafe($_REQUEST['full_name']);
		if($data['full_name'] == empty_lookup_value){ $data['full_name'] = ''; }
	$data['session_id'] = makeSafe($_REQUEST['session_id']);
		if($data['session_id'] == empty_lookup_value){ $data['session_id'] = ''; }
	$data['selectedID']=makeSafe($selected_id);

	// hook: speakers_before_update
	if(function_exists('speakers_before_update')){
		$args=array();
		if(!speakers_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `speakers` set       `full_name`=' . (($data['full_name'] !== '' && $data['full_name'] !== NULL) ? "'{$data['full_name']}'" : 'NULL') . ', `session_id`=' . (($data['session_id'] !== '' && $data['session_id'] !== NULL) ? "'{$data['session_id']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="speakers_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: speakers_after_update
	if(function_exists('speakers_after_update')){
		$res = sql("SELECT * FROM `speakers` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!speakers_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='speakers' and pkValue='".makeSafe($selected_id)."'", $eo);

}

